import React from 'react'
import {  Item } from 'semantic-ui-react'

import Avatar from 'react-avatar';
 

function toNumberString(num) { 
  if (Number.isInteger(num)) { 
    return num + ".0"
  } else {
    return num.toString(); 
  }
}
  
     
               

const ItemExampleItems = (props) => (

  <div class="item">
<div class="image">
<div id="A" title="Bad" style="width: 120px; height: 120px; line-height: initial; text-align: center; color: rgb(255, 255, 255); background: red;">
  <div id="B"style="display: table; table-layout: fixed; width: 100%; height: 100%; font-size: 40px;">
  <span id="c"style="display: table-cell; vertical-align: middle; white-space: nowrap;"><span>B</span></span></div></div>
</div>
<div class="content">

  <div class="meta">
    <span>DifficultyS:{toNumberString(props.list.difficulty)}</span>
  </div>
  <div class="meta">
    <span>Would Take Again:{props.list.takeAgain}</span>
  </div>
  <div class="description">
    <p>
    {props.list.body} 

    </p>
  </div>
  <div class="extra">
    Additional Details
  </div>
</div>
</div>




)





export default ItemExampleItems
