import React from 'react'; 
import SingleReview from './SingleReview';
import "./Review.css";

var  average = 0;
var count  = 0; 
var countYes = 0; 

const ReviewList = (props) => {

  
  const images = props.list.map(msg => {

    if(msg.takeAgain == "Yes"){
      countYes++;
    }

    average+=msg.difficulty;
    count++; 
    
    return <SingleReview list={msg} />;
  });

  return (
    <div> 
      <div class="ui segment" >
              <div class="ui statistic">
          <div class="value">
            {(average/count).toFixed(1) }
          </div>
          <div class="label">
            Average Score from {count} reviews
          </div>
        </div>

        <div class="ui statistic">
          <div class="value">
            {(countYes/count)*100}% 
          </div>
          <div class="label">
            Would take the course again
          </div>
        </div>



      </div>


  <div id="Review"className="ui items "> {images}</div>)
  </div>

  )
  
}


export default ReviewList;
