import React, { Component } from 'react'
import { Button, Modal, TextArea, Form } from 'semantic-ui-react'

class ModalExampleCloseConfig extends Component {
  state = { 
      open: false,
      message:false

}



  closeConfigShow = (closeOnEscape, closeOnDimmerClick) => () => {
    this.setState({ closeOnEscape, closeOnDimmerClick, open: true })
  }

  close = () => this.setState({ open: false})
  Submitclose = () => this.setState({ open: false, message:true })

  render() {
    const { open, closeOnEscape, closeOnDimmerClick } = this.state

    if(this.state.message){
        return(
            <div>
            <i onClick={this.closeConfigShow(false, true)}   class=" large flag outline icon"></i>
        
            <Modal
            open={open}
            closeOnEscape={closeOnEscape}
            closeOnDimmerClick={closeOnDimmerClick}
            onClose={this.close}
            >
            <Modal.Header>Report this post</Modal.Header>
            <Modal.Content>

            Thanks for letting us now, we will check this post out!

            </Modal.Content>
            <Modal.Actions>
            
                <Button
                onClick={this.Submitclose}
                positive
                labelPosition='right'
                icon='checkmark'
                content='Submit'
                />
            </Modal.Actions>

            </Modal>
        </div>
        );










    



    }




    if(this.props.on == true){

    return (

                <div>
                    <i onClick={this.closeConfigShow(false, true)}   class=" large flag outline icon"></i>
                
                    <Modal
                    open={open}
                    closeOnEscape={closeOnEscape}
                    closeOnDimmerClick={closeOnDimmerClick}
                    onClose={this.close}
                    >
                    <Modal.Header>Report this post</Modal.Header>
                    <Modal.Content>

                    <Form>
  
                    <Form.Field
                     
                        control={TextArea}
                    
                        placeholder='What was inappropriate about this post?'
                    />
                    </Form>

                    </Modal.Content>
                    <Modal.Actions>
                    
                        <Button
                        onClick={this.Submitclose}
                        positive
                        labelPosition='right'
                        icon='checkmark'
                        content='Submit'
                        />
                    </Modal.Actions>

                    </Modal>
                </div>
                )
            }

                return(


                    
    <i class=" large flag icon"></i>
                );
  }
}

export default ModalExampleCloseConfig
