import React from 'react'
import {  Item } from 'semantic-ui-react'

import Avatar from 'react-avatar';
 

function toNumberString(num) { 
  if (Number.isInteger(num)) { 
    return num + ".0"
  } else {
    return num.toString(); 
  }
}
  
     
               

const ItemExampleItems = (props) => (

  <div class="item">
<div class="image">
<Avatar size="120" color='red' name={toNumberString(props.list.difficulty)} />
</div>
<div class="content">

  <div class="meta">
    <span>DifficultyS:{props.list.difficulty}</span>
  </div>
  <div class="meta">
    <span>Would Take Again:{props.list.takeAgain}</span>
  </div>
  <div class="description">
    <p>
    {props.list.body} 

    </p>
  </div>
  <div class="extra">
    Additional Details
  </div>
</div>
</div>




)





export default ItemExampleItems
